import { useState } from "react";
import { motion, AnimatePresence } from "framer-motion";

export default function Chatbot() {
  const [messages, setMessages] = useState([
    { type: "bot", text: "ðŸ‘‹ Hi! I'm your SMG Scooter Assistant. How can I help you today?" },
  ]);
  const [input, setInput] = useState("");

  const suggestions = [
    "Model Specifications",
    "Accessories",
    "Insurance",
    "E-Fluids",
    "Finance Options",
  ];

  const getBotReply = (text) => {
    const lower = text.toLowerCase();
    if (lower.includes("model") || lower.includes("spec"))
      return "We offer multiple models with ranges up to 150km! ðŸš€";
    if (lower.includes("accessories"))
      return "We have helmets, chargers, seat covers and more! ðŸ§°";
    if (lower.includes("insurance"))
      return "We partner with top insurance providers for coverage. ðŸ›¡ï¸";
    if (lower.includes("emi") || lower.includes("finance"))
      return "Yes! Financing is available through Bajaj, HDFC, and more. ðŸ’³";
    if (lower.includes("fluid"))
      return "We offer special e-fluids for optimal battery health! ðŸ”‹";
    return "I'm not sure I understand. Try picking a topic below! ðŸ¤–";
  };

  const sendMessage = (text) => {
    setMessages((prev) => [...prev, { type: "user", text }]);
    setTimeout(() => {
      const reply = getBotReply(text);
      setMessages((prev) => [...prev, { type: "bot", text: reply }]);
    }, 600);
  };

  const handleSend = () => {
    if (!input.trim()) return;
    sendMessage(input.trim());
    setInput("");
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-100 via-white to-pink-100 flex items-center justify-center p-4">
      <div className="w-full max-w-md bg-white/80 backdrop-blur-md border border-gray-200 rounded-3xl shadow-xl p-6">
        {/* Header */}
        <div className="mb-6 text-center">
          <h2 className="text-3xl font-bold text-gray-800 mb-1">SMG Scooter Assistant</h2>
          <p className="text-sm text-gray-500">Your personal EV guide ðŸ¤–</p>
        </div>

        {/* Chat messages */}
        <div className="h-80 overflow-y-auto rounded-xl bg-white border p-4 shadow-inner scroll-smooth mb-4">
          <AnimatePresence>
            {messages.map((msg, i) => (
              <motion.div
                key={i}
                initial={{ opacity: 0, y: 8 }}
                animate={{ opacity: 1, y: 0 }}
                exit={{ opacity: 0 }}
                transition={{ duration: 0.3 }}
                className={`flex mb-3 ${msg.type === "user" ? "justify-end" : "justify-start"}`}
              >
                <div
                  className={`max-w-[75%] px-4 py-2 rounded-2xl text-sm ${
                    msg.type === "user"
                      ? "bg-blue-600 text-white rounded-br-none"
                      : "bg-gray-200 text-gray-800 rounded-bl-none"
                  } shadow`}
                >
                  {msg.text}
                </div>
              </motion.div>
            ))}
          </AnimatePresence>
        </div>

        {/* Suggestion buttons */}
        <div className="flex flex-wrap gap-2 mb-4">
          {suggestions.map((text, i) => (
            <button
              key={i}
              onClick={() => sendMessage(text)}
              className="text-sm px-3 py-1 border border-blue-500 text-blue-500 rounded-full hover:bg-blue-50 transition"
            >
              {text}
            </button>
          ))}
        </div>

        {/* Input */}
        <div className="flex gap-2">
          <input
            className="flex-grow border border-gray-300 rounded-full px-4 py-2 text-sm focus:outline-none focus:ring-2 focus:ring-blue-400 shadow"
            placeholder="Ask something..."
            value={input}
            onChange={(e) => setInput(e.target.value)}
            onKeyDown={(e) => e.key === "Enter" && handleSend()}
          />
          <button
            className="bg-blue-600 hover:bg-blue-700 transition text-white px-5 py-2 rounded-full text-sm shadow"
            onClick={handleSend}
          >
            Send
          </button>
        </div>
      </div>
    </div>
  );
}
