import React, { useState } from "react";
import config from "../config";
import ""
function ChatBox() {
  const [messages, setMessages] = useState([]);
  const [query, setQuery] = useState("");

  const askBot = async () => {
    if (!query.trim()) return;

    const userMessage = { text: query, sender: "user" };
    setMessages((prev) => [...prev, userMessage]);

    try {
      const res = await fetch(`${config.API_URL}/ask`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ query }),
      });

      const data = await res.json();
      const botMessage = { text: data.response, sender: "bot" };
      setMessages((prev) => [...prev, botMessage]);
    } catch (err) {
      setMessages((prev) => [
        ...prev,
        { text: "⚠️ Failed to get a response.", sender: "bot" },
      ]);
      console.error(err);
    }

    setQuery(""); // Clear input field
  };

  const handleKeyPress = (event) => {
    if (event.key === "Enter" && !event.shiftKey) {
      event.preventDefault();
      askBot();
    } else if (event.key === "Enter" && event.shiftKey) {
      setQuery((prev) => prev + "\n");
    }
  };

  return (
    <div className="max-w-2xl mx-auto p-4 flex flex-col h-[90vh]">
      <div className="flex-1 overflow-y-auto space-y-2 p-4 bg-gray-100 rounded-xl shadow-inner">
        {messages.map((msg, index) => (
          <div
            key={index}
            className={`max-w-[75%] p-3 rounded-2xl text-white ${
              msg.sender === "user"
                ? "bg-blue-600 self-end"
                : "bg-gray-700 self-start"
            }`}
          >
            {msg.text}
          </div>
        ))}
      </div>

      <div className="mt-4 flex items-end gap-2">
        <textarea
          value={query}
          onChange={(e) => setQuery(e.target.value)}
          onKeyDown={handleKeyPress}
          placeholder="Type your message..."
          rows="2"
          className="flex-1 resize-none p-2 rounded-lg border border-gray-300 focus:outline-none focus:ring-2 focus:ring-blue-500"
        />
        <button
          onClick={askBot}
          className="bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded-lg shadow-md transition-all"
        >
          Send
        </button>
      </div>
    </div>
  );
}

export default ChatBox;
