import React, { useState, useEffect } from "react";
import config from "../config"; 
import "./ChatBox.css"; 

function ChatBox() {
  const [messages, setMessages] = useState([]);
  const [query, setQuery] = useState("");
  const [selectedModel, setSelectedModel] = useState("gemini-1.5-pro-latest"); // Default model

  // Load chat history from local storage
  useEffect(() => {
    const savedMessages = JSON.parse(localStorage.getItem("chatHistory")) || [];
    setMessages(savedMessages);
  }, []);

  // Save chat history to local storage
  useEffect(() => {
    localStorage.setItem("chatHistory", JSON.stringify(messages));
  }, [messages]);

  const askBot = async () => {
    if (!query.trim()) return;

    const userMessage = { text: query, sender: "user", timestamp: new Date().toLocaleTimeString() };
    setMessages((prev) => [...prev, userMessage]);

    const res = await fetch(`${config.API_URL}/ask`, {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ query, model: selectedModel }), // Send selected AI model
    });
    const data = await res.json();

    const botMessage = { text: data.response, sender: "bot", timestamp: new Date().toLocaleTimeString() };
    setMessages((prev) => [...prev, botMessage]);
    setQuery(""); // Clear input field
  };

  const startNewChat = () => {
    setMessages([]);
    localStorage.removeItem("chatHistory"); // Clears local storage
  };

  return (
    <div className="chat-container">
      {/* Sidebar for Chat History & Model Selection */}
      <div className="chat-sidebar">
        <h3>Chats</h3>
        <button className="new-chat-btn" onClick={startNewChat}>ðŸ†• New Chat</button>
        
        <h4>Chat History</h4>
        {messages.map((msg, index) => (
          msg.sender === "user" && (
            <div key={index} className="history-item" onClick={() => setQuery(msg.text)}>
              {msg.text} - <small>{msg.timestamp}</small>
            </div>
          )
        ))}

        <h4>Select Chat Model</h4>
        <select value={selectedModel} onChange={(e) => setSelectedModel(e.target.value)}>
          <option value="gemini-1.5-pro-latest">Gemini 1.5</option>
          <option value="chatgpt-4">ChatGPT-4</option>
          <option value="claude-3">Claude 3</option>
        </select>
      </div>

      {/* Main Chat Section */}
      <div className="chat-box">
        {messages.map((msg, index) => (
          <div key={index} className={`message ${msg.sender}`}>
            {msg.text} <small className="timestamp">{msg.timestamp}</small>
          </div>
        ))}
      </div>

      {/* Input Section */}
      <div className="chat-input">
        <textarea
          value={query}
          onChange={(e) => setQuery(e.target.value)}
          onKeyDown={(event) => event.key === "Enter" && !event.shiftKey && (event.preventDefault(), askBot())}
          placeholder="Type your message..."
          rows="2"
        />
        <button onClick={askBot}>Send</button>
      </div>
    </div>
  );
}

export default ChatBox;
